contracts/DonationContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract DonationContract is Ownable, ReentrancyGuard {
    struct Donation {
        address donor;
        address token; // address(0) for ETH
        uint256 amount;
        uint256 timestamp;
    }

    constructor(address initialOwner) Ownable(initialOwner) {}

    struct UserDonations {
        uint256 totalETH;
        mapping(address => uint256) tokenBalances; // token address => amount
    }

    mapping(string => Donation[]) private postDonations; // streamId => donations
    mapping(address => UserDonations) private userDonations; // user => donations received
    mapping(string => address) private streamToAuthor; // streamId => post author

    event DonationReceived(
        string indexed streamId,
        address indexed donor,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    event DonationWithdrawn(
        address indexed user,
        address indexed token,
        uint256 amount
    );

    function donateETH(string memory streamId) external payable {
        require(msg.value > 0, "Must send ETH");
        require(bytes(streamId).length > 0, "Invalid stream ID");

        address author = streamToAuthor[streamId];
        if (author == address(0)) {
            author = msg.sender;
            streamToAuthor[streamId] = author;
        }

        postDonations[streamId].push(
            Donation({
                donor: msg.sender,
                token: address(0),
                amount: msg.value,
                timestamp: block.timestamp
            })
        );

        userDonations[author].totalETH += msg.value;

        emit DonationReceived(
            streamId,
            msg.sender,
            address(0),
            msg.value,
            block.timestamp
        );
    }

    function donateToken(
        string memory streamId,
        address token,
        uint256 amount
    ) external {
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Amount must be greater than 0");
        require(bytes(streamId).length > 0, "Invalid stream ID");

        IERC20 tokenContract = IERC20(token);
        require(
            tokenContract.transferFrom(msg.sender, address(this), amount),
            "Transfer failed"
        );

        address author = streamToAuthor[streamId];
        if (author == address(0)) {
            author = msg.sender;
            streamToAuthor[streamId] = author;
        }

        postDonations[streamId].push(
            Donation({
                donor: msg.sender,
                token: token,
                amount: amount,
                timestamp: block.timestamp
            })
        );

        userDonations[author].tokenBalances[token] += amount;

        emit DonationReceived(
            streamId,
            msg.sender,
            token,
            amount,
            block.timestamp
        );
    }

    function withdrawETH() external nonReentrant {
        uint256 amount = userDonations[msg.sender].totalETH;
        require(amount > 0, "No ETH to withdraw");

        userDonations[msg.sender].totalETH = 0;

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "ETH transfer failed");

        emit DonationWithdrawn(msg.sender, address(0), amount);
    }

    function withdrawToken(address token) external nonReentrant {
        require(token != address(0), "Invalid token address");

        uint256 amount = userDonations[msg.sender].tokenBalances[token];
        require(amount > 0, "No tokens to withdraw");

        userDonations[msg.sender].tokenBalances[token] = 0;

        IERC20 tokenContract = IERC20(token);
        require(tokenContract.transfer(msg.sender, amount), "Transfer failed");

        emit DonationWithdrawn(msg.sender, token, amount);
    }

    function getDonationsByPost(
        string memory streamId
    ) external view returns (Donation[] memory) {
        return postDonations[streamId];
    }

    function getUserETHBalance(address user) external view returns (uint256) {
        return userDonations[user].totalETH;
    }

    function getUserTokenBalance(
        address user,
        address token
    ) external view returns (uint256) {
        return userDonations[user].tokenBalances[token];
    }

    function getPostAuthor(
        string memory streamId
    ) external view returns (address) {
        return streamToAuthor[streamId];
    }

    // Allow contract to receive ETH
    receive() external payable {}
}

contracts/ERC2771Context.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/Context.sol";

/**
 * @dev Context variant with ERC2771 support.
 */
abstract contract ERC2771Context is Context {
    address private immutable _trustedForwarder;

    constructor(address trustedForwarder) {
        _trustedForwarder = trustedForwarder;
    }

    function isTrustedForwarder(address forwarder) public view virtual returns (bool) {
        return forwarder == _trustedForwarder;
    }

    function _msgSender() internal view virtual override returns (address sender) {
        if (isTrustedForwarder(msg.sender)) {
            // The assembly code is more direct than the Solidity version using `abi.decode`.
            /// @solidity memory-safe-assembly
            assembly {
                sender := shr(96, calldataload(sub(calldatasize(), 20)))
            }
        } else {
            return super._msgSender();
        }
    }

    function _msgData() internal view virtual override returns (bytes calldata) {
        if (isTrustedForwarder(msg.sender)) {
            return msg.data[:msg.data.length - 20];
        } else {
            return super._msgData();
        }
    }
}

contracts/MinimalForwarder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";

/**
 * @dev Simple minimal forwarder to be used together with an ERC2771 compatible contract. See {ERC2771Context}.
 */
contract MinimalForwarder is EIP712 {
    using ECDSA for bytes32;

    struct ForwardRequest {
        address from;
        address to;
        uint256 value;
        uint256 gas;
        uint256 nonce;
        bytes data;
        uint256 deadline;
    }

    bytes32 private constant _TYPEHASH =
        keccak256("ForwardRequest(address from,address to,uint256 value,uint256 gas,uint256 nonce,bytes data,uint256 deadline)");

    mapping(address => uint256) private _nonces;

    constructor() EIP712("MinimalForwarder", "0.0.1") {}

    function getNonce(address from) public view returns (uint256) {
        return _nonces[from];
    }

    function verify(ForwardRequest calldata req, bytes calldata signature) public view returns (bool) {
        address signer = _hashTypedDataV4(
            keccak256(abi.encode(
                _TYPEHASH,
                req.from,
                req.to,
                req.value,
                req.gas,
                req.nonce,
                keccak256(req.data),
                req.deadline
            ))
        ).recover(signature);
        return _nonces[req.from] == req.nonce && signer == req.from;
    }

    function execute(
        ForwardRequest calldata req,
        bytes calldata signature
    ) public payable returns (bool, bytes memory) {
        require(verify(req, signature), "MinimalForwarder: signature does not match request");
        require(req.deadline == 0 || req.deadline >= block.timestamp, "MinimalForwarder: request expired");

        _nonces[req.from] = req.nonce + 1;

        (bool success, bytes memory returndata) = req.to.call{gas: req.gas, value: req.value}(
            abi.encodePacked(req.data, req.from)
        );

        // Validate that the relayer has sent enough gas for the call.
        // See https://ronan.eth.limo/blog/ethereum-gas-dangers/
        if (gasleft() <= req.gas / 63) {
            // We explicitly trigger invalid opcode to consume all gas and bubble-up the effects, since
            // neither revert or assert consume all gas since Solidity 0.8.0
            // https://docs.soliditylang.org/en/v0.8.0/control-structures.html#panic-via-assert-and-error-via-require
            /// @solidity memory-safe-assembly
            assembly {
                invalid()
            }
        }

        return (success, returndata);
    }
}

contracts/PointsContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ERC2771Context.sol";

contract PointsContract is Ownable, ERC2771Context {
    struct UserPoints {
        uint256 totalPoints;
        mapping(string => uint256) postPoints;
    }

    constructor(
        address initialOwner,
        address trustedForwarder
    ) Ownable(initialOwner) ERC2771Context(trustedForwarder) {}

    mapping(address => UserPoints) private userPoints;
    mapping(string => address) private streamToAuthor;

    uint256 public constant POINTS_PER_POST = 10;
    uint256 public constant POINTS_PER_COMMENT = 5;
    uint256 public constant POINTS_PER_LIKE = 2;
    uint256 public constant POINTS_PER_REPOST = 3;

    event PostCreated(address indexed user, string streamId, uint256 points);
    event CommentAdded(address indexed user, string streamId, uint256 points);
    event LikeAdded(address indexed user, string streamId, uint256 points);
    event RepostAdded(address indexed user, string streamId, uint256 points);
    event PointsAdded(address indexed user, uint256 amount, string reason);

    function createPost(string memory streamId) external {
        require(bytes(streamId).length > 0, "Invalid stream ID");
        require(streamToAuthor[streamId] == address(0), "Post already exists");

        streamToAuthor[streamId] = _msgSender();
        userPoints[_msgSender()].postPoints[streamId] = POINTS_PER_POST;
        userPoints[_msgSender()].totalPoints += POINTS_PER_POST;

        emit PostCreated(_msgSender(), streamId, POINTS_PER_POST);
    }

    function addComment(string memory streamId) external {
        require(bytes(streamId).length > 0, "Invalid stream ID");
        require(streamToAuthor[streamId] != address(0), "Post does not exist");

        userPoints[_msgSender()].totalPoints += POINTS_PER_COMMENT;
        emit CommentAdded(_msgSender(), streamId, POINTS_PER_COMMENT);
    }

    function addLike(string memory streamId) external {
        require(bytes(streamId).length > 0, "Invalid stream ID");
        require(streamToAuthor[streamId] != address(0), "Post does not exist");

        userPoints[_msgSender()].totalPoints += POINTS_PER_LIKE;
        emit LikeAdded(_msgSender(), streamId, POINTS_PER_LIKE);
    }

    function addRepost(string memory streamId) external {
        require(bytes(streamId).length > 0, "Invalid stream ID");
        require(streamToAuthor[streamId] != address(0), "Post does not exist");

        userPoints[_msgSender()].totalPoints += POINTS_PER_REPOST;
        emit RepostAdded(_msgSender(), streamId, POINTS_PER_REPOST);
    }

    function getUserPoints(address user) external view returns (uint256) {
        return userPoints[user].totalPoints;
    }

    function getPostPoints(
        string memory streamId
    ) external view returns (uint256) {
        address author = streamToAuthor[streamId];
        require(author != address(0), "Post does not exist");
        return userPoints[author].postPoints[streamId];
    }

    function addPoints(
        address user,
        uint256 amount,
        string memory reason
    ) external onlyOwner {
        require(user != address(0), "Invalid user address");
        require(amount > 0, "Amount must be greater than 0");

        userPoints[user].totalPoints += amount;
        emit PointsAdded(user, amount, reason);
    }

    function getPostAuthor(
        string memory streamId
    ) external view returns (address) {
        return streamToAuthor[streamId];
    }

